import { useNetworkInformation } from '@usehooks.io/use-network-information';

function NetworkStatus() {
  const { networkInfo, isOnline, isSupported, refresh } = useNetworkInformation();

  if (!isSupported) {
    return <div>Network Information API not supported</div>;
  }

  return (
    <div>
      <p>Online: {isOnline ? 'Yes' : 'No'}</p>
      {networkInfo && (
        <div>
          <p>Connection Type: {networkInfo.type}</p>
          <p>Effective Type: {networkInfo.effectiveType}</p>
          <p>Downlink: {networkInfo.downlink} Mbps</p>
          <p>RTT: {networkInfo.rtt} ms</p>
          <p>Save Data: {networkInfo.saveData ? 'Yes' : 'No'}</p>
        </div>
      )}
      <button onClick={refresh}>Refresh</button>
    </div>
  );
}
