import { useRoleGuard } from '@usehooks.io/use-role-guard';

function AdminPanel() {
  const { hasAccess, isLoading, user, hasAnyRole } = useRoleGuard(
    ['admin', 'moderator'],
    {
      redirectTo: '/login',
      onUnauthorized: () => console.log('Access denied')
    }
  );

  if (isLoading) {
    return <div>Loading...</div>;
  }

  if (!hasAccess) {
    return <div>Access denied</div>;
  }

  return (
    <div>
      <h1>Admin Panel</h1>
      <p>Welcome, {user?.id}</p>
      {hasAnyRole(['admin']) && (
        <button>Admin Only Action</button>
      )}
    </div>
  );
}
