import { useVibration } from '@usehooks.io/use-vibration';

function NotificationComponent() {
  const { vibrate, stop, isSupported } = useVibration();

  const handleNotification = () => {
    // Single vibration for 200ms
    vibrate(200);
  };

  const handleUrgentNotification = () => {
    // Pattern: vibrate 100ms, pause 50ms, vibrate 100ms, pause 50ms, vibrate 200ms
    vibrate([100, 50, 100, 50, 200]);
  };

  const handleError = () => {
    // Long vibration for errors
    vibrate(500);
  };

  if (!isSupported) {
    return <div>Vibration not supported on this device</div>;
  }

  return (
    <div>
      <button onClick={handleNotification}>
        Normal Notification
      </button>

      <button onClick={handleUrgentNotification}>
        Urgent Notification
      </button>

      <button onClick={handleError}>
        Error Notification
      </button>

      <button onClick={stop}>
        Stop Vibration
      </button>
    </div>
  );
}
