import { useWebShare } from '@usehooks.io/use-web-share';

function ArticleShare() {
  const { share, canShare, isLoading, error, isSupported, clearError } = useWebShare();

  const shareArticle = async () => {
    const shareData = {
      title: 'Amazing Article',
      text: 'Check out this amazing article!',
      url: window.location.href
    };

    if (canShare(shareData)) {
      const success = await share(shareData);
      if (success) {
        console.log('Article shared successfully!');
      }
    }
  };

  const shareWithFiles = async () => {
    const file = new File(['Hello World'], 'hello.txt', { type: 'text/plain' });

    const shareData = {
      title: 'Shared File',
      files: [file]
    };

    await share(shareData);
  };

  if (!isSupported) {
    return <div>Web Share API not supported</div>;
  }

  return (
    <div>
      <button onClick={shareArticle} disabled={isLoading}>
        {isLoading ? 'Sharing...' : 'Share Article'}
      </button>

      <button onClick={shareWithFiles} disabled={isLoading}>
        Share File
      </button>

      {error && (
        <div style={{ color: 'red' }}>
          Error: {error}
          <button onClick={clearError}>Clear</button>
        </div>
      )}
    </div>
  );
}
